mapper.update:
update
    seckill
set
    number = number - 1
where seckill_id = #{seckillId}
    and start_time  <=  #{killTime}
    and end_time >= #{killTime}
    and number > 0;

mapper.select:
select seckill_id, name, number, start_time, end_time, create_time
from seckill
where seckill_id = #{seckillId};
